AJS.toInit(function(a){a(".colour-input").change(function(){a("#swatch."+this.id).css("background-color",this.value)});a(".colour-image").click(function(){var c=this.id.split("image.");var b=c[1];window.open(AJS.params.contextPath+"/colourpicker.action?colourKey="+b,"colourPicker","menubar=yes,location=no,personalbar=no,scrollbar=yes,width=580,height=300,resizable")});a("#edit-scheme-link").click(function(){a("#edit-scheme").toggleClass("hidden");return false})});