if(!document.getElementById){document.getElementById=function(){return null}}function initializeMenu(d,a){var c=document.getElementById(d);var b=document.getElementById(a);if(c==null||b==null){return}b.parentNode.style.backgroundImage="url("+contextPath+"/images/icons/tree_plus.gif)";b.onclick=function(){var e=c.style.display;this.parentNode.style.backgroundImage=(e=="block")?"url("+contextPath+"/images/icons/tree_plus.gif)":"url("+contextPath+"/images/icons/tree_minus.gif)";c.style.display=(e=="block")?"none":"block";return false}}function menuStatus(b,e,a){var d=document.getElementById(e);var c=document.getElementById(a);if(d==null||c==null){return}c.parentNode.style.backgroundImage=(b=="close")?"url("+contextPath+"/images/icons/tree_plus.gif)":"url("+contextPath+"/images/icons/tree_minus.gif)";d.style.display=(b=="close")?"none":"block";return}function openAll(){var c=document.getElementsByTagName("ul");for(var b=0;b<c.length;b++){var a=c[b].id;if(a.substring(0,5)=="menu-"){var d=a.substring(5,a.length);menuStatus("open","menu-"+d,"actuator-"+d)}}}function closeAll(){var c=document.getElementsByTagName("ul");for(var b=0;b<c.length;b++){var a=c[b].id;if(a.substring(0,5)=="menu-"){var d=a.substring(5,a.length);menuStatus("close","menu-"+d,"actuator-"+d)}}};